class Solution {
public:
struct state {
        int fi_val;
        int fi_ind;
        int se_val;
        int se_ind;
        int th_val;

        state() {
            fi_ind = -1;
            se_ind = -1;
        }
    };
    vector<vector<int>> val_mat;
    vector<vector<int>> ind_mat;
    vector<state> infos;
    Solution() : val_mat(3, vector<int>(3)), ind_mat(3, vector<int>(3)), infos(3) {};



    long long f() {
        long long ans = INT64_MIN;
        for (int i = 0; i < 3; i++) {
            val_mat[i][0] = infos[i].fi_val;
            val_mat[i][1] = infos[i].se_val;
            val_mat[i][2] = infos[i].th_val;
            ind_mat[i][0] = infos[i].fi_ind;
            ind_mat[i][1] = infos[i].se_ind;
            ind_mat[i][2] = i+500;
        }
        for (int i = 0; i < 3; i++) {
            for(int j = 0; j < 3; j++) {
                for(int k = 0; k < 3; k++) {
                    if(ind_mat[0][i] == ind_mat[1][j] || ind_mat[0][i] == ind_mat[2][k] || ind_mat[1][j] == ind_mat[2][k]) continue;
                    ans = max(ans, (long long) val_mat[0][i] + val_mat[1][j] + val_mat[2][k]);
                }
            }
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<state> max_info(m);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                state& info = max_info[i];
                if (info.fi_ind == -1 || board[i][j] > info.fi_val) {
                    info.th_val = info.se_val;
                    info.se_val = info.fi_val;
                    info.se_ind = info.fi_ind;
                    info.fi_val = board[i][j];
                    info.fi_ind = j;
                } else if (info.se_ind == -1 || board[i][j] > info.se_val) {
                    info.th_val = info.se_val;
                    info.se_val = board[i][j];
                    info.se_ind = j;
                } else if (board[i][j] > info.th_val) {
                    info.th_val = board[i][j];
                }
            }
        }
        long long ans = INT64_MIN;
        for (int i = 0; i < m; i++) {
            infos[0] = max_info[i];
            for (int j = i + 1; j < m; j++) {
                infos[1] = max_info[j];
                for (int k = j + 1; k < m; k++) {
                   infos[2] = max_info[k];
                    ans = max(ans, f());
                }
            }
        }
        return ans;
    }
};
