class Solution {
public:
    // helper to call in
    long long helper(vector<vector<int>>& matrix) {
        int rows = matrix.size();
        int cols = matrix[0].size();
        long long maxSum = LLONG_MIN;
    // two vectors
        vector<vector<long long>> topValues(rows, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> topColumns(rows, vector<int>(3, -1));

        // top 3 value
        for (int row = 0; row < rows; ++row) {
            vector<pair<long long, int>> columnValues;

            for (int col = 0; col < cols; ++col) {
                columnValues.emplace_back(matrix[row][col], col);
            }
            // sort kar 
            sort(columnValues.rbegin(), columnValues.rend()); 
                // store values
            for (int rank = 0; rank < 3 && rank < cols; ++rank) {
                topColumns[row][rank] = columnValues[rank].second;
                topValues[row][rank] = columnValues[rank].first;

            }
        }

        // all combination
        for (int row1 = 0; row1 < rows; ++row1) {
            for (int row2 = row1 + 1; row2 < rows; ++row2) {
                for (int row3 = row2 + 1; row3 < rows; ++row3) {
                    for (int col1 = 0; col1 < 3; ++col1) {
                        for (int col2 = 0; col2 < 3; ++col2) {
                            if (topColumns[row2][col2] == topColumns[row1][col1]) continue;
                            for (int col3 = 0; col3 < 3; ++col3) {
                                if (topColumns[row3][col3] == topColumns[row1][col1] || topColumns[row3][col3] == topColumns[row2][col2]) continue;

                                long long currentSum = topValues[row1][col1] + topValues[row2][col2] + topValues[row3][col3];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }
        // final ans
        return maxSum;
    }
    // call helper
    long long maximumValueSum(vector<vector<int>>& matrix) {
        return helper(matrix);
    }
};

