#include <bits/stdc++.h>
using namespace std;
#define fi first
#define se second
#define sz(x) (int)x.size()
#define all(x) (x).begin(),(x).end()
typedef pair<int,int> PII;
const int inf = 0x3f3f3f3f;

class Solution {
    vector<vector<int>> g;
    int n,m,mx;
    vector<PII> p;
    long long ans = -3e9 - 1;

    void dfs(int i,long long sum) {
    	if (1LL * sum + 1LL * mx * (3 - sz(p)) < ans) {
    		return;
    	}
        if (sz(p) >= 3) {
        	ans = max(ans,sum);
        	return;
        }
        if (i >= n) return;
        auto check = [&](int j) -> bool {
        	for (auto & e : p) {
        		if (e.se == j) return false;
        	}
        	return true;
        };
        for (int j = 0; j < m; j++) {
        	if (check(j)) {
        		p.push_back({i,j});
        		dfs(i + 1,sum + g[i][j]);
        		p.pop_back();
        	}
        }
        dfs(i + 1,sum);
    }
public:
    long long maximumValueSum(vector<vector<int>>& g) {
        this -> g = g;
        n = g.size();
        m = g[0].size();
        for (auto & e : g) {
        	mx = max(mx,*max_element(all(e)));
        }
        dfs(0,0LL);
        return ans;
    }
};
