class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        typedef pair<int, int> PII;
        vector<vector<PII>> maxv(m, vector<PII>(3, {-1, -1}));

        for (int i = 0; i < m; i++) {
            vector<PII> tmp;
            for (int j = 0; j < n; j++) tmp.push_back({board[i][j], j});
            sort(tmp.begin(), tmp.end(), greater<PII>());

            for (int j = 0; j < 3; j++) maxv[i][j] = tmp[j];
        }

        long long res = LLONG_MIN;
        for (int x1 = 0; x1 < m; x1++) {
            for (int y1 = 0; y1 < 3; y1++) {
                for (int x2 = 0; x2 < m; x2++) {
                    if (x2 == x1) continue;
                    for (int y2 = 0; y2 < 3; y2++) {
                        if (maxv[x2][y2].second == maxv[x1][y1].second) continue;
                        for (int x3 = 0; x3 < m; x3++) {
                            if (x3 == x1 || x3 == x2) continue;
                            for (int y3 = 0; y3 < 3; y3++) {
                                if (maxv[x3][y3].second == maxv[x1][y1].second || maxv[x3][y3].second == maxv[x2][y2].second) continue;
                                res = max(res, (long long)maxv[x1][y1].first + maxv[x2][y2].first + maxv[x3][y3].first);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
