class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& grid) {
        int numRows = grid.size();
        int numCols = grid[0].size();
        long long highestSum = LLONG_MIN;
        vector<vector<long long>> topRowValues(numRows, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> topColumnIndices(numRows, vector<int>(3, -1));

        for (int row = 0; row < numRows; ++row) {
            vector<pair<long long, int>> valueColumnPairs;
            for (int col = 0; col < numCols; ++col) {
                valueColumnPairs.emplace_back(grid[row][col], col);
            }
            sort(valueColumnPairs.rbegin(), valueColumnPairs.rend());

            for (int idx = 0; idx < 3 && idx < numCols; ++idx) {
                topRowValues[row][idx] = valueColumnPairs[idx].first;
                topColumnIndices[row][idx] = valueColumnPairs[idx].second;
            }
        }

        for (int r1 = 0; r1 < numRows; ++r1) {
            for (int r2 = r1 + 1; r2 < numRows; ++r2) {
                for (int r3 = r2 + 1; r3 < numRows; ++r3) {
                    for (int c1 = 0; c1 < 3; ++c1) {
                        for (int c2 = 0; c2 < 3; ++c2) {
                            if (topColumnIndices[r2][c2] == topColumnIndices[r1][c1]) continue;
                            for (int c3 = 0; c3 < 3; ++c3) {
                                if (topColumnIndices[r3][c3] == topColumnIndices[r1][c1] || topColumnIndices[r3][c3] == topColumnIndices[r2][c2]) continue;

                                long long currentSum = topRowValues[r1][c1] + topRowValues[r2][c2] + topRowValues[r3][c3];
                                highestSum = max(highestSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return highestSum;
    }
};
