    long long solve1(vector<vector<int>>& board,int i,int prev1,int prev2,int n,int m,vector<vector<vector<long long int>>> &dp){
        if(i==n){
            return LONG_MIN;
        }

        if(dp[i][prev1+1][prev2+1]!=-1){
            return dp[i][prev1+1][prev2+1];
        }
        long long k1=solve1(board,i+1,prev1,prev2,n,m,dp);

        for(int j=0;j<m;j++){
            if(prev1==-1){
                long long k2=solve1(board,i+1,j,prev2,n,m,dp);
                if(k2!=LONG_MIN){
                    k1=max(k1,board[i][j]+k2);
                }

            }else if(prev2==-1 && prev1!=j){
                long long k2=solve1(board,i+1,prev1,j,n,m,dp);
                if(k2!=LONG_MIN){
                    k1=max(k1,board[i][j]+k2);
                }

            }else if(prev1!=j && prev2!=j){
                k1=max((long long)board[i][j],k1);
            }
        }

        return dp[i][prev1+1][prev2+1]=k1;
    }
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();

        vector<int> vis(m+1,0);
        vector<vector<vector<long long int>>> dp(n+1,vector<vector<long long int>> (m+1,vector<long long int> (m+1,-1)));
        return solve1(board,0,-1,-1,n,m,dp);

    }
