class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        vector<tuple<int,int,int>> v;
        for(int i=0;i<m;i++) {
            for(int j=0;j<n;j++) {
                v.push_back(make_tuple(-(board[i][j]),i,j));
            }
        }

        sort(v.begin(), v.end());

        long long score=-1000000000000;
        int hi = min(3*(n+m), (int)v.size());
        for(int i=0;i<hi;i++) {

            int highest_k = 1000000;
            //cout << get<0>(v[i]) << " " << get<1>(v[i]) << " " << get<2>(v[i]) << "\n";

            for(int j=i+1;j<hi;j++) {

                if(j > highest_k) break;
                //cout << "BP1\n";
                if((get<1>(v[j]) == get<1>(v[i])) || (get<2>(v[j]) == get<2>(v[i]))) continue;
                //cout << "BP2\n";

                for(int k=j+1;k<hi;k++) {

                    if(get<1>(v[j]) == get<1>(v[k]) || get<2>(v[j]) == get<2>(v[k])) continue;
                    if(get<1>(v[i]) == get<1>(v[k]) || get<2>(v[i]) == get<2>(v[k])) continue;

                    long long newscore = -( (long long)(get<0>(v[i])) + (long long)(get<0>(v[j])) + (long long)(get<0>(v[k])) );
                    if(newscore > score) {
                        score = newscore;
                        highest_k = k;
                    }
                    break;

                }
            }
        }

        return score;







    }
};
