class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector <vector <pair<int, int>>> row_max(n, vector <pair<int, int>>(3, {INT_MIN, -1}));

        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(board[i][j] > row_max[i][0].first){
                    row_max[i][2] = row_max[i][1];
                    row_max[i][1] = row_max[i][0];
                    row_max[i][0] = {board[i][j], j};
                }
                else if(board[i][j] > row_max[i][1].first){
                     row_max[i][2] = row_max[i][1];
                     row_max[i][1] = {board[i][j], j};
                }
                else if(board[i][j] > row_max[i][2].first){
                    row_max[i][2] = {board[i][j], j};
                }
            }
        }

        long long ans = LLONG_MIN;
        for(int row1 = 0; row1<n; row1++){
            for(int x=0; x<3; x++){
                for(int row2=0; row2<n; row2++){
                    if(row2==row1) continue;
                    for(int y=0; y<3; y++){
                        if(row_max[row2][y].second ==  row_max[row1][x].second) continue;
                        for(int row3=0; row3<n; row3++){
                            if(row3==row1 || row3==row2) continue;
                            for(int z=0; z<3; z++){
                                if(row_max[row3][z].second ==  row_max[row1][x].second) continue;
                                if(row_max[row3][z].second ==  row_max[row2][y].second) continue;
                                ans = max(ans, row_max[row1][x].first + 0LL+ row_max[row2][y].first + 0LL+ row_max[row3][z].first);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
