type node struct {
	x, y, val int
}

const inf = 0x3f3f3f3f

func maximumValueSum(board [][]int) int64 {
	m, n := len(board), len(board[0])

	rowMax := make([][]node, m)
	colMax := make([][]node, n)

	for i := 0; i < m; i++ {
		rowMax[i] = []node{{-1, -1, -inf}, {-1, -1, -inf}, {-1, -1, -inf}}
	}
	for j := 0; j < n; j++ {
		colMax[j] = []node{{-1, -1, -inf}, {-1, -1, -inf}, {-1, -1, -inf}}
	}

	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			val := board[i][j]
			updateTop3(&rowMax[i], node{i, j, val})
			updateTop3(&colMax[j], node{i, j, val})
		}
	}

	res := -inf * 3
	for i := 0; i < m; i++ {
		for j := i + 1; j < m; j++ {
			for k := j + 1; k < m; k++ {
				for _, c1 := range rowMax[i] {
					for _, c2 := range rowMax[j] {
						for _, c3 := range rowMax[k] {
							if c1.y != c2.y && c1.y != c3.y && c2.y != c3.y {
								sum := c1.val + c2.val + c3.val
								if sum > res {
									res = sum
								}
							}
						}
					}
				}
			}
		}
	}

	return int64(res)
}

func updateTop3(top3 *[]node, newNode node) {
	*top3 = append(*top3, newNode)
	sort.Slice(*top3, func(i, j int) bool {
		return (*top3)[i].val > (*top3)[j].val
	})
	*top3 = (*top3)[:3]
}
