class Solution {
public:
    void fio(){
    ios::sync_with_stdio(false); // Speeds up input/output
    cin.tie(nullptr); // Unties cin from cout for faster input
}
    long long maximumValueSum(vector<vector<int>>& board) {

        fio();

        int r = board.size();
        int c = board[0].size();
        long long res = LLONG_MIN;

        vector<vector<pair<long long, int>>> max_row(r);
        for (int i = 0; i < r; ++i) {
            for (int j = 0; j < c; ++j) {
                max_row[i].emplace_back(board[i][j], j);
            }
            sort(max_row[i].rbegin(), max_row[i].rend());
            max_row[i].resize(3);
        }

        for (int x = 0; x < r; ++x) {
    for (int y = x + 1; y < r; ++y) {
        for (int z = y + 1; z < r; ++z) {
            for (int i = 0; i < 3; ++i) {
                for (int j = 0; j < 3; ++j) {
                    if (max_row[y][j].second == max_row[x][i].second) continue;
                    for (int k = 0; k < 3; ++k) {
                        if (max_row[z][k].second == max_row[x][i].second || max_row[z][k].second == max_row[y][j].second) continue;

                        long long ans = max_row[x][i].first + max_row[y][j].first + max_row[z][k].first;
                        res = max(res, ans);
                    }
                }
            }
        }
    }
}

        return res;
    }
};
