class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m = len(board)
        n = len(board[0])

        vals = []
        for i in range(m):
            for j in range(n):
                vals.append((board[i][j], 1 << i, 1 << j))

        vals.sort(key=lambda x: x[0], reverse=True)
        tuple_scores = []
        max_tuple_score = vals[0][0] + vals[1][0]
        max_score = -math.inf
        for i in range(len(vals)):
            if vals[i][0] + max_tuple_score <= max_score:
                break

            tuple_scores_len = len(tuple_scores)
            for j in range(tuple_scores_len):
                if vals[i][1] & tuple_scores[j][1] == 0 and vals[i][2] & tuple_scores[j][2] == 0:
                    score = vals[i][0] + tuple_scores[j][0]
                    if score > max_score:
                        max_score = score

            for j in range(i):
                if vals[i][1] & vals[j][1] == 0 and vals[i][2] & vals[j][2] == 0:
                    tuple_scores.append((vals[i][0] + vals[j][0], vals[i][1] | vals[j][1], vals[i][2] | vals[j][2]))

        return max_score
