package main

import (
	"math"
	"sort"
)

func maximumValueSum(b [][]int) int64 {
	r := len(b)
	c := len(b[0])
	m := int64(math.MinInt64)
	tv := make([][]int64, r)
	ti := make([][]int, r)

	for i := range tv {
		tv[i] = make([]int64, 3)
		ti[i] = make([]int, 3)
		for j := range tv[i] {
			tv[i][j] = int64(math.MinInt64)
			ti[i][j] = -1
		}
	}

	for i := 0; i < r; i++ {
		cv := make([]struct {
			v int64
			idx int
		}, c)
		for j := 0; j < c; j++ {
			cv[j] = struct {
				v int64
				idx int
			}{int64(b[i][j]), j}
		}
		sort.Slice(cv, func(a, b int) bool {
			return cv[a].v > cv[b].v
		})

		for k := 0; k < 3 && k < c; k++ {
			tv[i][k] = cv[k].v
			ti[i][k] = cv[k].idx
		}
	}

	x := 0
	for x < r {
		y := x + 1
		for y < r {
			z := y + 1
			for z < r {
				a := 0
				for a < 3 {
					b := 0
					for b < 3 {
						if ti[y][b] == ti[x][a] {
							b++
							continue
						}
						c := 0
						for c < 3 {
							if ti[z][c] == ti[x][a] || ti[z][c] == ti[y][b] {
								c++
								continue
							}

							cs := tv[x][a] + tv[y][b] + tv[z][c]
							if cs > m {
								m = cs
							}

							c++
						}
						b++
					}
					a++
				}
				z++
			}
			y++
		}
		x++
	}

	return m
}
