class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        unordered_map<long long,set<pair<long long,long long>>>mpp;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(mpp[i].size()<3){
                    mpp[i].insert({board[i][j],j});
                }
                else{
                    if(board[i][j]>mpp[i].begin()->first){
                        mpp[i].erase(mpp[i].begin());
                        mpp[i].insert({board[i][j],j});
                    }
                }
            }
        }

        long long ans=-1e12;

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    // if(i!=j && j!=k && i!=k){

                        for(auto it1:mpp[i]){
                            for(auto it2:mpp[j]){
                                for(auto it3:mpp[k]){
                                    if(it1.second!=it2.second && it2.second!=it3.second && it1.second!=it3.second){
                                        ans=max(ans,it1.first+it2.first+it3.first+0ll);
                                    }
                                }
                            }
                        }

                    // }
                }
            }
        }
        return ans;
    }
};
