class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<int>> max_idx(board.size(), vector<int>(3, -1));
        for(int i = 0; i < board.size(); i++) {
            for(int j = 0; j < board[0].size(); j++) {
                if(max_idx[i][0] == -1 || board[i][max_idx[i][0]] <= board[i][j]) {
                    max_idx[i][2] = max_idx[i][1];
                    max_idx[i][1] = max_idx[i][0];
                    max_idx[i][0] = j;
                }
                else if(max_idx[i][1] == -1 || board[i][max_idx[i][1]] <= board[i][j]) {
                    max_idx[i][2] = max_idx[i][1];
                    max_idx[i][1] = j;
                }
                else if(max_idx[i][2] == -1 || board[i][max_idx[i][2]] <= board[i][j]) {
                    max_idx[i][2] = j;
                }
            }
        }
        long long res = INT64_MIN;
        for(int i = 0; i < board.size(); i++) {
            for(int j = 0; j < i; j++) {
                for(int k = 0; k < j; k++) {
                    for(int i1: max_idx[i]) {
                        for(int i2: max_idx[j]) {
                            for(int i3: max_idx[k]) {
                                if (i1 != i2 && i2 != i3 && i1 != i3) {
                                    res = max(res, (long long)board[i][i1] + board[j][i2] +board[k][i3]);
                                }
                            }
                        }
                    }
                }
            }
        }
        return res;

    }
};
