#define ll long long int
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
         int m = board.size();
        int n = board[0].size();
        ll ans = LLONG_MIN;
        vector<vector<ll>>vec(m, vector<ll>(3, LLONG_MIN));
        vector<vector<int>>vec2(m, vector<int>(3, -1));
        for (int i = 0; i < m; ++i) {
            vector<pair<ll,int>>v;
            for (int j = 0; j < n; ++j){
                v.emplace_back(board[i][j], j);
            }
            sort(v.rbegin(),v.rend()); 
            for (int k = 0; k < 3 && k < n; ++k) {
                vec[i][k]=v[k].first;
                vec2[i][k]=v[k].second;
            }
        }
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (vec2[r2][j]==vec2[r1][i]) 
                                continue;
                            for (int k = 0; k < 3; ++k) {
                                if (vec2[r3][k]==vec2[r1][i] || vec2[r3][k]==vec2[r2][j]) 
                                    continue;
                                ll res=vec[r1][i]+vec[r2][j]+vec[r3][k];
                                ans=max(res,ans);
                            }
                        }
                    }
                }
            }
        }
        return ans;
    }
};
