class Solution:
    def maximumValueSum(self, g: List[List[int]]) -> int:
        m, n = len(g), len(g[0])
        firsts = [float('-inf')] * n
        seconds = [float('-inf')] * n
        last_choices = [-1] * n
        minors = [float('-inf')] * n
        minor_choices = [-1] * n

        res = float('-inf')

        for i in range(m):
            old_firsts = firsts[:]
            old_seconds = seconds[:]
            old_last_choices = last_choices[:]
            old_minors = minors[:]
            old_minor_choices = minor_choices[:]

            for j in range(n):
                firsts[j] = max(old_firsts[j], g[i][j])

            if i > 0:
                for j in range(n):
                    for k in range(n):
                        if j == k:
                            continue

                        total = g[i][j] + old_firsts[k]
                        if total >= seconds[j]:
                            if minor_choices[j] == -1 or k != last_choices[j]:
                                minors[j] = seconds[j]
                                minor_choices[j] = last_choices[j]

                            seconds[j] = total
                            last_choices[j] = k

                        elif total > minors[j] and k != last_choices[j]:
                            minors[j] = total
                            minor_choices[j] = k

            if i > 1:
                for j in range(n):
                    for k in range(n):
                        if j == k:
                            continue

                        total = g[i][j] + (old_seconds[k] if j != old_last_choices[k] else old_minors[k])
                        res = max(res, total)

        return res
