class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();

        // 存储每行的最大值、次大值、第三大值及其列索引
        vector<array<pair<int, int>, 3>> rowMax(m);

        for (int i = 0; i < m; ++i) {
            // 初始化为最小值
            int firstMax = INT_MIN, secondMax = INT_MIN, thirdMax = INT_MIN;
            int firstIdx = -1, secondIdx = -1, thirdIdx = -1;

            for (int j = 0; j < n; ++j) {
                int value = board[i][j];

                if (value > firstMax) {
                    thirdMax = secondMax;
                    thirdIdx = secondIdx;

                    secondMax = firstMax;
                    secondIdx = firstIdx;

                    firstMax = value;
                    firstIdx = j;
                } else if (value > secondMax) {
                    thirdMax = secondMax;
                    thirdIdx = secondIdx;

                    secondMax = value;
                    secondIdx = j;
                } else if (value > thirdMax) {
                    thirdMax = value;
                    thirdIdx = j;
                }
            }

            rowMax[i] = {{ {firstMax, firstIdx}, {secondMax, secondIdx}, {thirdMax, thirdIdx} }};
        }

        long long maxSum = LLONG_MIN;

        // 三重循环选择三个不同的行
        for (int i = 0; i < m; ++i) {
            for (int j = i + 1; j < m; ++j) {
                for (int k = j + 1; k < m; ++k) {
                    // 尝试每一行的最大、次大、第三大值组合
                    for (auto& x1 : rowMax[i]) {
                        for (auto& x2 : rowMax[j]) {
                            if (x1.second == x2.second) continue; // 确保列不冲突
                            for (auto& x3 : rowMax[k]) {
                                if (x3.second == x1.second || x3.second == x2.second) continue; // 确保列不冲突
                                long long sum = (long long)x1.first + x2.first + x3.first;
                                maxSum = max(maxSum, sum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
