class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();

        vector<vector<pair<long long,pair<long long,long long>>>> maxis(n);

        for(int i = 0;i<n;i++){
            vector<pair<int,pair<int,int>>> v;
            for(int j = 0;j<m;j++){
               v.push_back({board[i][j] , {i,j}});
            }
            sort(v.begin(),v.end());
            maxis[i].push_back(v[m-1]);
            maxis[i].push_back(v[m-2]);
            maxis[i].push_back(v[m-3]);
        }

        long long ans = LLONG_MIN;

        for(int i = 0;i<n;i++){
            for(int j = 0;j<3;j++){
                for(int i2 = i+1;i2<n;i2++){
                    for(int j2 = 0;j2<3;j2++){
                        for(int i3 = i2+1;i3<n;i3++){
                            for(int j3 = 0;j3<3;j3++){
                                if(maxis[i][j].second.second != maxis[i2][j2].second.second && maxis[i2][j2].second.second != maxis[i3][j3].second.second && maxis[i3][j3].second.second != maxis[i][j].second.second)
                                ans = max(ans,maxis[i][j].first + maxis[i2][j2].first + maxis[i3][j3].first);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
