class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
           long long ans=-30000000000;
           long long m=board.size(),n=board[0].size();
          vector<vector<pair< long long int, long long int>>>v;
           for(int i=0;i<m;i++)
           {
                 vector<pair< long long int, long long int>>v1;
                 for(int j=0;j<n;j++)
                       v1.push_back({board[i][j],j});
                 v.push_back(v1);
           }


                vector<vector<pair< long long int, long long int>>>v2(m);
        for(int i=0;i<m;i++)
        {
             sort(v[i].begin(),v[i].end());
             for(int j=n-1;j>=(n-1-2);j--)
                   v2[i].push_back(v[i][j]);
        }
        // for(int i=0;i<m;i++)
        // {
        //      for(int j=0;j<3;j++)
        //            cout<<v2[i][j].first<<v2[i][j].second<<" ";
        //     cout<<endl;
        // }
        for(int i=0;i<m;i++)
        {
            for(int i1=0;i1<3;i1++)
            {
                long long temp1=v2[i][i1].first;

                 long long ind1=v2[i][i1].second;
                  for(int j=i+1;j<m;j++)
                    {

                       for(int j1=0;j1<3;j1++)
                       {
                            long long temp2=v2[j][j1].first;
                             long long ind2=v2[j][j1].second;
                           if(ind1==ind2)
                                 continue;
                            for(int k=j+1;k<m;k++)
                               {

                                 for(int k1=0;k1<3;k1++)
                                  {
                                     long long temp3=v2[k][k1].first;
                                     int ind3=v2[k][k1].second;
                                      if(ind1==ind3 || ind2==ind3)
                                       continue;
                                       ans=max(ans,temp1+temp2+temp3);
                                   }

                              }   
                       }
                    }   
            }

        }
        return ans;

    }
};
