long long dp[102][102][102];
class Solution {
public:
    long long rec(int ind,int a,int b,vector<vector<int>> &board){
        if(ind>=board.size()) return -1e12;
        if(dp[ind][a][b]!=-1) return dp[ind][a][b];
        long long ans=rec(ind+1,a,b,board);
        for(int i=0;i<board[0].size();i++){
            if(a==0){
                ans=max(ans,board[ind][i]+rec(ind+1,i+1,b,board));
            }
            else if(b==0){
                if(a!=(i+1)) ans=max(ans,board[ind][i]+rec(ind+1,a,i+1,board));
            }
            else if(a!=(i+1) && b!=(i+1) && a!=0 && b!=0){
                ans=max(ans,board[ind][i]+0ll);
            }
        }
        return dp[ind][a][b]=ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        for(int i=0;i<board.size()+1;i++){
            for(int j=0;j<=board[0].size()+1;j++){
                for(int k=0;k<=board[0].size()+1;k++){
                    dp[i][j][k]=-1;
                }
            }
        }
        return rec(0,0,0,board);
    }
};
