class Solution {
public:
    long long f(vector<pair<long long int,long long int>>&a,vector<pair<long long int,long long int>>&b,vector<pair<long long int,long long int>>&c){
        long long int ans=-1e15;
        for(int cnt1=0;cnt1<3;cnt1++){
            for(int cnt2=0;cnt2<3;cnt2++){
                for(int cnt3=0;cnt3<3;cnt3++){
                    int index1=a[cnt1].second;
                    int index2=b[cnt2].second;
                    int index3=c[cnt3].second;
                    if(index1!=index2 && index2!=index3 && index3!=index1){
                        ans=max(ans,a[cnt1].first+b[cnt2].first+c[cnt3].first);
                    }
                }
            }
        }
        return ans;
    }
