class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        vector<pair<int, pair<int, int>>> v;

        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                v.push_back({board[i][j], {i, j}});
            }
        }

        sort(v.begin(), v.end());
        long long sum = LLONG_MIN;  // Use long long for sum
        reverse(v.begin(), v.end());

        for(int i = 0; i < v.size(); i++) {
            long long a = v[i].first;  // Use long long for a
            int a1 = v[i].second.first;
            int a2 = v[i].second.second;

            for(int j = i + 1; j < v.size(); j++) {
                long long b = v[j].first;  // Use long long for b
                int b1 = v[j].second.first;
                int b2 = v[j].second.second; 
                if(b1 == a1 || b2 == a2) continue;

                for(int k = j + 1; k < v.size(); k++) {
                    long long c = v[k].first;  // Use long long for c
                    int c1 = v[k].second.first;
                    int c2 = v[k].second.second;
                    if(c1 == b1 || c1 == a1 || c2 == b2 || c2 == a2) continue;

                    sum = max(sum, a + b + c);
                    break;
                }

            }
        }

        return sum;
    }
};
