#define ll long long
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size(),m=a[0].size();
        ll ans=a[0][0]*1ll + a[1][1]*1ll + a[2][2]*1ll;
        vector<pair<ll,pair<ll,ll>>> d;
        for(ll i=0;i<n;i++)
        {
            for(ll j=0;j<m;j++)
            {
                d.push_back({a[i][j]*1ll,{i,j}});
            }
        }
        sort(d.begin(),d.end());
        ll first=d.size()-203;
        first=max(first,0ll);
        for(ll i=d.size()-1;i>=0 && i>=first;i--)
        {
            ll fir=d[i].first;
            ll x=d[i].second.first,y=d[i].second.second;
            ll second=i-203;
            second=max(second,0ll);
            for(ll j=i-1;j>=0 && j>=i-1-400 && j>=second;j--)
            {
                ll sec=d[j].first;
                ll xx=d[j].second.first,yy=d[j].second.second;

                if(x == xx)continue;
                if(y == yy)continue;
                ll third=j-203;
                third=max(third,0ll);
                for(ll k=j-1;k>=0 && k>=third;k--)
                {
                    ll thi=d[k].first;
                    ll xxx=d[k].second.first,yyy=d[k].second.second;

                    if(xxx == x)continue;
                    if(xxx == xx)continue;
                    if(yyy == y)continue;
                    if(yyy == yy)continue;
                    ll anss=fir+sec+thi;
                    // cout<<anss<<" "<<x<<" "<<y<<" "<<xx<<" "<<yy<<" "<<xxx<<" "<<yyy<<"\n";
                    if(anss>ans)ans=anss;
                }
            }
        }
        // cout<<"\n\n";
        return ans;
    }
};
