class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        int m = b.size();
        int n = b[0].size();
        long long maxi = LLONG_MIN;

        vector<vector<long long>> vals(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> cols(m, vector<int>(3, -1));

        for (int r = 0; r < m; ++r) {
            vector<pair<long long, int>> v;
            for (int c = 0; c < n; ++c) {
                v.emplace_back(b[r][c], c);
            }
            sort(v.rbegin(), v.rend()); 

            for (int i = 0; i < 3 && i < n; i++) {
                vals[r][i] = v[i].first;
                cols[r][i] = v[i].second;
            }
        }

        for (int r1 = 0; r1 < m; r1++) {
            for (int r2 = r1 + 1; r2 < m; r2++) {
                for (int r3 = r2 + 1; r3 < m; r3++) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (cols[r2][j] == cols[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (cols[r3][k] == cols[r1][i] || cols[r3][k] == cols[r2][j]) continue;

                                long long cur = vals[r1][i] + vals[r2][j] + vals[r3][k];
                                maxi = max(maxi, cur);
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }
};
