package main

import (
	"math"
	"sort"
)

func maximumValueSum(v [][]int) int64 {
	m := len(v)
	n := len(v[0])

	type c struct {
		v int64
		r int
		l int
	}

	var cs []c

	for i := 0; i < m; i++ {
		for j := 0; j < n; j++ {
			cs = append(cs, c{v: int64(v[i][j]), r: i, l: j})
		}
	}

	sort.Slice(cs, func(i, j int) bool {
		return cs[i].v > cs[j].v
	})

	ms := int64(math.MinInt64)

	for i := 0; i < len(cs); i++ {
		r1, l1 := cs[i].r, cs[i].l
		s1 := cs[i].v

		for j := i + 1; j < len(cs); j++ {
			r2, l2 := cs[j].r, cs[j].l
			if r1 == r2 || l1 == l2 {
				continue
			}
			s2 := s1 + cs[j].v

			for k := j + 1; k < len(cs); k++ {
				r3, l3 := cs[k].r, cs[k].l
				if r1 == r3 || l1 == l3 || r2 == r3 || l2 == l3 {
					continue
				} else {
					s3 := s2 + cs[k].v
					if s3 > ms {
						ms = s3
					}
					break
				}
			}
		}
	}

	return ms
}
