class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length, n = board[0].length;
        int[][] arr = new int[m*n][3];
        for(int i=0; i<m; i++) {
            for(int j=0, k=i*n; j<n; j++, k++) {
                arr[k][0] = i;
                arr[k][1] = j;
                arr[k][2] = board[i][j];
            }
        }
        Arrays.sort(arr, (a,b) -> b[2]-a[2]);
        // for(int[] a:arr)
            // System.out.println(Arrays.toString(a));
        long max = Long.MIN_VALUE;
        for(int i=0; i<m*n; i++) {
            for(int j=i+1; j<m*n; j++) {
                if(arr[i][0] == arr[j][0] || arr[i][1] == arr[j][1])
                    continue;
                long sum = arr[i][2] + arr[j][2];
                if(sum+arr[j][2] <= max)
                    break;
                for(int k=j+1; k<m*n; k++) {
                    if(sum+arr[k][2] <= max)
                        break;
                    if(arr[i][0] == arr[k][0] || arr[i][1] == arr[k][1])
                        continue;
                    if(arr[j][0] == arr[k][0] || arr[j][1] == arr[k][1])
                        continue;
                    // System.out.printf("i:%d, j:%d, k:%d, max:%d, sum:%d\n", i, j, k, max, sum);
                    max = Math.max(max, sum+arr[k][2]);
                    break;
                }
            }
        }
        return max;
        // return solve(board, new boolean[board[0].length], 0, 3);
    }

    long solve(int[][] board, boolean[] colMask, int curRow, int count) {
        if(count==0)
            return 0;
        if(curRow == board.length)
            return Long.MIN_VALUE;
        long max = Long.MIN_VALUE;
        for(int i=0; i<board[0].length; i++) {
            if(!colMask[i]) {
                colMask[i] = true;
                max = Math.max(max, solve(board, colMask, curRow+1, count-1)+board[curRow][i]);
                colMask[i] = false;
            }
        }
        // memo[]
        return max;
    }
}
