class Solution {
public:
      long long maximumValueSum(vector<vector<int>>& grid) {
     int r = grid.size();
int c = grid[0].size();
long long m = LLONG_MIN;
vector<vector<long long>> v(r, vector<long long>(3, m));
vector<vector<int>> p(r, vector<int>(3, -1));

for (int i = 0; i < r; ++i) {
    vector<pair<long long, int>> q;
    for (int j = 0; j < c; ++j) {
        q.emplace_back(grid[i][j], j);
    }
    sort(q.rbegin(), q.rend()); 

    for (int k = 0; k < 3 && k < c; ++k) {
        v[i][k] = q[k].first;
        p[i][k] = q[k].second;
    }
}


for (int x = 0; x < r; ++x) {
    for (int y = x + 1; y < r; ++y) {
        for (int z = y + 1; z < r; ++z) {
            for (int a = 0; a < 3; ++a) {
                for (int b = 0; b < 3; ++b) {
                    if (p[y][b] == p[x][a]) continue;
                    for (int d = 0; d < 3; ++d) {
                        if (p[z][d] == p[x][a] || p[z][d] == p[y][b]) continue;

                        long long s = v[x][a] + v[y][b] + v[z][d];
                        m = max(m, s);
                    }
                }
            }
        }
    }
}

        int count=0;
for(auto x:grid){
   count++;
}
return m;
    }
};
