class Solution {
    fun maximumValueSum(board: Array<IntArray>): Long {
        val m = board.size
        val n = board[0].size
        var maxSum = Long.MIN_VALUE

        val bestValues = Array(m) { LongArray(3) { Long.MIN_VALUE } }
        val bestColumns = Array(m) { IntArray(3) { -1 } }

        for (i in 0 until m) {
            val colValues = board[i].mapIndexed { j, value -> value.toLong() to j }
            val sortedColValues = colValues.sortedByDescending { it.first }

            for (k in 0 until 3) {
                if (k < n) {
                    bestValues[i][k] = sortedColValues[k].first
                    bestColumns[i][k] = sortedColValues[k].second
                }
            }
        }

        // Try all combinations of 3 different rows
        for (r1 in 0 until m) {
            for (r2 in (r1 + 1) until m) {
                for (r3 in (r2 + 1) until m) {
                    for (i in 0 until 3) {
                        for (j in 0 until 3) {
                            if (bestColumns[r2][j] == bestColumns[r1][i]) continue
                            for (k in 0 until 3) {
                                if (bestColumns[r3][k] == bestColumns[r1][i] || bestColumns[r3][k] == bestColumns[r2][j]) continue

                                val currentSum = bestValues[r1][i] + bestValues[r2][j] + bestValues[r3][k]
                                maxSum = maxOf(maxSum, currentSum)
                            }
                        }
                    }
                }
            }
        }

        return maxSum
    }
}
