class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int rows = board.size();
        int cols = board[0].size();
        long long maxSum = LLONG_MIN;

        // Stores the best 3 values and their corresponding column indices for each row
        vector<vector<long long>> topValues(rows, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> topColumns(rows, vector<int>(3, -1));

        // Identify the top 3 values in each row
        for (int row = 0; row < rows; ++row) {
            vector<pair<long long, int>> columnValues;
            for (int col = 0; col < cols; ++col) {
                columnValues.emplace_back(board[row][col], col);
            }
            sort(columnValues.rbegin(), columnValues.rend());

            for (int k = 0; k < min(3, cols); ++k) {
                topValues[row][k] = columnValues[k].first;
                topColumns[row][k] = columnValues[k].second;
            }
        }

        // Evaluate all combinations of three different rows
        for (int r1 = 0; r1 < rows; ++r1) {
            for (int r2 = r1 + 1; r2 < rows; ++r2) {
                for (int r3 = r2 + 1; r3 < rows; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topColumns[r2][j] == topColumns[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (topColumns[r3][k] == topColumns[r1][i] || topColumns[r3][k] == topColumns[r2][j]) continue;

                                long long currentSum = topValues[r1][i] + topValues[r2][j] + topValues[r3][k];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
