class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        long long length1 = b.size();
        long long length2 = b[0].size();
        long long ans = LLONG_MIN;
        vector<vector<pair<long long, long long>>> matrix(length2);
        for (long long j = 0; j < length2; j++) {
            vector<pair<long long, long long>> v;
            for (long long i = 0; i < length1; i++) {
                v.push_back({b[i][j], i});
            }
            sort(v.rbegin(), v.rend());
            for (long long i = 0; i < min(3LL, length1); i++) {
                matrix[j].push_back(v[i]);
            }
        }
        for (long long i = 0; i < length2; i++) {
            for (long long j = i + 1; j < length2; j++) {
                for (long long k = j + 1; k < length2; k++) {
                    for (auto& l : matrix[i]) {
                        for (auto& p : matrix[j]) {
                            for (auto& q : matrix[k]) {
                                if (l.second != p.second && l.second != q.second && p.second != q.second) {
                                    long long result = l.first + p.first + q.first;
                                    ans = max(ans, result);
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
