class Solution {
public:
  long long maximumValueSum(vector<vector<int>>& g) {
    int m = g.size(), n = g[0].size(), mn = m*n;
      vector<vector<int>> a; //(mn, vector<int>(3));
      for(int i = 0; i<m; ++i) for(int j = 0; j<n; ++j) {
        a.push_back({g[i][j], i, j});
      }
    sort(a.begin(), a.end()); long long ans = LLONG_MIN;
    for(int k = 1; k<=max(9, max(m , n)); ++k) {
      long long cur = a[mn-k][0];
      int ax = a[mn-k][1], ay = a[mn-k][2];
      for(int z = mn-k-1, find = 5; ~z && find; --z) if(a[z][1] != ax && a[z][2] != ay){
        --find;
        long long cur1 = cur + a[z][0];
       int bx = a[z][1], by = a[z][2];
        for(int o = z-1; ~o; --o) if(a[o][1] != ax && a[o][2] != ay &&
                                     a[o][1] != bx && a[o][2] != by){
          ans = max(ans, cur1 + a[o][0]) ;
        }
      } 
    }
    return ans;
  }
};
