public class Solution {
    public long maximumValueSum(int[][] board) {
        int rows = board.length;
        int cols = board[0].length;
        long maxSum = Long.MIN_VALUE;

        long[][] maxValues = new long[rows][3];
        int[][] columnIndices = new int[rows][3];

        for (int i = 0; i < rows; i++) {
            List<Pair> columnValues = new ArrayList<>();
            for (int j = 0; j < cols; j++) {
                columnValues.add(new Pair(board[i][j], j));
            }
            columnValues.sort((a, b) -> Long.compare(b.value, a.value)); 

            for (int k = 0; k < 3 && k < cols; k++) {
                maxValues[i][k] = columnValues.get(k).value;
                columnIndices[i][k] = columnValues.get(k).index;
            }
        }

        for (int row1 = 0; row1 < rows; row1++) {
            for (int row2 = row1 + 1; row2 < rows; row2++) {
                for (int row3 = row2 + 1; row3 < rows; row3++) {
                    for (int i = 0; i < 3; i++) {
                        for (int j = 0; j < 3; j++) {
                            if (columnIndices[row2][j] == columnIndices[row1][i]) continue;
                            for (int k = 0; k < 3; k++) {
                                if (columnIndices[row3][k] == columnIndices[row1][i] || columnIndices[row3][k] == columnIndices[row2][j]) continue;

                                long currentSum = maxValues[row1][i] + maxValues[row2][j] + maxValues[row3][k];
                                maxSum = Math.max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }

    private static class Pair {
        long value;
        int index;

        Pair(long value, int index) {
            this.value = value;
            this.index = index;
        }
    }
}
