typedef long long ll;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        vector<vector<int>> f(n, vector<int>(m, 0));
        for (int j = 0; j < m; j ++) {
            vector<pair<int,int>> v;
            for (int i = 0; i < n; i ++) {
                v.push_back({a[i][j], i});
            }
            sort(v.begin(), v.end());
            reverse(v.begin(), v.end());
            for (int i = 0; i < 3; i ++) {
                f[v[i].second][j] = 1;
            }
        }
        vector<vector<int>> p(n);
        vector<vector<int>> vec;
        for (int i = 0; i < n; i ++) {
            vector<pair<int,int>> v;
            for (int j = 0; j < m; j ++) {
                v.push_back({a[i][j], j});
            }
            sort(v.begin(), v.end());
            reverse(v.begin(), v.end());
            for (int j = 0; j < min(m, 3); j ++) { 
                if (f[i][v[j].second]) {
                    p[i].push_back(v[j].second);
                    vec.push_back({v[j].first, i, v[j].second});
                }
            }
        }
        sort(vec.begin(), vec.end());
        reverse(vec.begin(), vec.end());


        long long ans = -1e18;
        for (int i = 0; i < n; i ++) {
            for (int j = i + 1; j < n; j ++) {
                for (auto p1: p[i]) for (auto p2: p[j]) if (p1 != p2) {
                    for (auto info: vec) {
                        if (info[1] != i and info[1] != j and info[2] != p1 and info[2] != p2) {
                            ans = max(ans, (ll)info[0] + (ll)a[i][p1] + (ll)a[j][p2]);
                            break;
                        }
                    }   
                }
            }
        }
        return ans;
    }
};
