/**
 * @param {number[][]} board
 * @return {number}
 */
var maximumValueSum = function(board) {
    const cells = []
    for (let i = 0; i < board.length; i++) {
        for (let j = 0; j < board[0].length; j++) {
            cells.push([i, j])
        }
    }

    cells.sort((a, b) => board[b[0]][b[1]] - board[a[0]][a[1]]);

    const pickedRows = new Set(); 
    const pickedColumns = new Set();
    let searchEnd = cells.length;

    const search = (index, count) => {
        if (index === searchEnd) {
            return -Infinity;
        }

        const [r, c] = cells[index];
        if (pickedRows.has(r) || pickedColumns.has(c)) {
            return search(index + 1, count);
        }

        if (count === 1) {
            searchEnd = Math.min(index + 2, cells.length);
            return board[r][c];
        }

        // pick
        pickedRows.add(r);
        pickedColumns.add(c);
        const pick = board[r][c] + search(index + 1, count - 1);
        pickedRows.delete(r);
        pickedColumns.delete(c);

        const notPick = search(index + 1, count);

        return Math.max(pick, notPick);
    }

    return search(0, 3)
};
