long long dp[101][120][120] = {0LL};
class Solution {
public:
    map<pair<pair<int, int>, int>, long long> mp;
    long long max(long long a, long long b) {
        if(a > b) return a;
        else return b;
    }
    long long fun(int i, vector<vector<int>>& board, int column1, int column2, vector<vector<int>> &v1) {
        if(i >= board.size()) {
            return -1000000000000;
        }
        if(dp[i][column1 + 5][column2 + 5] != -1) {
            return dp[i][column1 + 5][column2 + 5];
        }
        if(column1 != -1 and column2 != -1) {
            long long ele = LLONG_MIN;
            for(int j = 0; j < board[0].size(); j++) {
                if(j == column1 or j == column2) continue;
                ele = max(ele, v1[i][j]);
            }
            return dp[i][column1 + 5][column2 + 5] = ele;
        }

        long long ret = LLONG_MIN;
        ret = fun(i + 1, board, column1, column2, v1);
        for(int j = 0; j < board[0].size(); j++) {
            if(j == column1 or j == column2) continue;
            int f = 0;
            if(column1 == -1) {
                f = 1;
                column1 = j;
            }
            else {
                f = 2;
                column2 = j;
            }
            ret = max(ret, board[i][j] + fun(i + 1, board, column1, column2, v1));
            if(f == 1) column1 = -1;
            else column2 = -1;
        }
        return dp[i][column1 + 5][column2 + 5] = ret;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<int>> v1 = board;
        for(int i = n - 2; i >= 0; i--) {
            for(int j = 0; j < m; j++) {
                v1[i][j] = max(v1[i][j], v1[i + 1][j]);
            }
        }
        for(int i = 0; i <= n; i++) {
            for(int j = 0; j <= m + 5; j++) {
                for(int k = 0; k <= m + 5; k++) {
                    dp[i][j][k] = -1;
                }
            }
        }
        return fun(0, board, -1, -1, v1);
    }
};
