class Solution {
public:
    int len = 500;
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        vector<tuple<int, int, int>> data;
        for(int i = 0; i < m; i++){
            for(int j = 0; j < n; j++){
                data.push_back(make_tuple(board[i][j], i , j));
            }
        }
        sort(data.begin(), data.end(), [](auto a, auto b){
            return get<0>(a) > get<0>(b);
        });
        long long res = -4 * 1e9;
        for(int i = 0; i < data.size(); i++){
            long long tmp1 = get<0>(data[i]);
            unordered_set<int> row;
            unordered_set<int> col;
            row.insert(get<1>(data[i]));
            col.insert(get<2>(data[i]));
            bool fin = false;
            long long now = -4 * 1e9;
            for(int j = i + 1; j < data.size(); j++){
                int value = get<0>(data[j]);
                int mr = get<1>(data[j]);
                int mc = get<2>(data[j]);
                if(row.count(mr) || col.count(mc)){
                    continue;
                }
                long long tmp = value;
                bool f1 = false, f2 = false;
                if(row.count(mr)){
                    f1 = true;
                }
                if(col.count(mc)){
                    f2 = true;
                }
                row.insert(mr);
                col.insert(mc);
                for(int k = j + 1; k < data.size(); k++){
                    int value = get<0>(data[k]);
                    int mr = get<1>(data[k]);
                    int mc = get<2>(data[k]);
                    if(row.count(mr) || col.count(mc)){
                        continue;
                    }
                    tmp += value;
                    now = max(now, tmp);
                    if(k == j + 1){
                        fin = true;
                    }
                    break;
                }
                if(fin) break;
                // 移除
                if(!f1){
                    row.erase(mr);
                }
                if(!f2){
                    col.erase(mc);
                }

            }
            res = max(res, tmp1 + now);
        } 
        return res;

    }
};
