class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        vector<vector<pair<int, int>>> pairs;
        for(int i=0; i<n; i++){
            int maxi = INT_MIN, maxi2 = INT_MIN, maxi3 = INT_MIN;
            int maxiInd = -1, maxi2Ind = -1, maxi3Ind = -1;
            for(int j=0; j<board[0].size(); j++){
                if(board[i][j] > maxi){
                    maxi3 = maxi2;
                    maxi3Ind = maxi2Ind;
                    maxi2 = maxi;
                    maxi2Ind = maxiInd;
                    maxi = board[i][j];
                    maxiInd = j;
                }
                else if(board[i][j] > maxi2){
                    maxi3 = maxi2;
                    maxi2 = board[i][j];
                    maxi3Ind = maxi2Ind;
                    maxi2Ind = j;
                }
                else if(board[i][j] > maxi3){
                    maxi3 = board[i][j];
                    maxi3Ind = j;
                }
            }
            pair<int, int> p1{maxi, maxiInd};
            pair<int, int> p2{maxi2, maxi2Ind};
            pair<int, int> p3{maxi3, maxi3Ind};
            vector<pair<int, int>> temp;
            temp.push_back(p1);
            temp.push_back(p2);
            temp.push_back(p3);
            pairs.push_back(temp);
        }


        long long maxSum = LLONG_MIN;

        for(int i=0; i<n; i++){
            for(int j=i+1; j<n; j++){
                for(int k=j+1; k<n; k++){
                    auto v1 = pairs[i], v2 = pairs[j], v3 = pairs[k];
                    for(int i1=0; i1<3; i1++){
                        for(int j1=0; j1<3; j1++){
                            for(int k1=0; k1<3; k1++){
                                if(v1[i1].second == v2[j1].second || v2[j1].second == v3[k1].second ||  v1[i1].second == v3[k1].second) continue;
                                maxSum = max(maxSum, (long long)v1[i1].first + v2[j1].first + v3[k1].first);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
