class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
         int m = board.size();
        int n = board[0].size();
        long long maxSum = LLONG_MIN;
        vector<vector<long long>> bValues(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> bColumns(m, vector<int>(3, -1));

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> colValue;
            for (int j = 0; j < n; ++j) {
                colValue.emplace_back(board[i][j], j);
            }
            sort(colValue.rbegin(), colValue.rend()); 

            for (int k = 0; k < 3 && k < n; ++k) {
                bValues[i][k] = colValue[k].first;
                bColumns[i][k] = colValue[k].second;
            }
        }

        // Try all combinations of all  3 different rows.
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (bColumns[r2][j] == bColumns[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (bColumns[r3][k] == bColumns[r1][i] || bColumns[r3][k] == bColumns[r2][j]) continue;

                                long long currentSum = bValues[r1][i] + bValues[r2][j] + bValues[r3][k];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
