class Solution {
public:

// 定义一个结构体来存储格子的行、列和值
struct Cell {
    int row;
    int col;
    long long value;
};

// 比较函数，用于按值排序
bool static compare(Cell a, Cell b) {
    return a.value > b.value; // 从大到小排序
}

long long maximumValueSum(vector<vector<int>>& board) {
    int m = board.size();
    int n = board[0].size();

    vector<Cell> cells;
    // 收集所有格子及其值
    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < n; ++j) {
            cells.push_back({i, j, board[i][j]});
        }
    }

    // 按值从大到小排序
    sort(cells.begin(), cells.end(), compare);

    long long maxValue = LLONG_MIN;

    // 遍历所有可能的三格组合
    int size = cells.size();
    for (int i = 0; i < size; ++i) {
        const Cell& c1 = cells[i];
        if (c1.value * 3 < maxValue) {
            break;
        }
        for (int j = i + 1; j < size; ++j) {
            const Cell& c2 = cells[j];
            if (c1.value + c2.value * 2 < maxValue) {
                break;
            } 
            for (int k = j + 1; k < size; ++k) {
                const Cell& c3 = cells[k];
                if (c1.value + c2.value + c3.value < maxValue) {
                    break;
                }

                // 检查三格是否互不攻击
                if (c1.row != c2.row && c1.row != c3.row && c2.row != c3.row &&
                    c1.col != c2.col && c1.col != c3.col && c2.col != c3.col) {

                    // 计算当前三格的值之和
                    long long currentSum = static_cast<long long>(c1.value) + c2.value + c3.value;
                    maxValue = max(maxValue, currentSum);
                }
            }
        }
    }

    return maxValue;
}
};
