class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) 
    {
        int m = board.size(), n = board[0].size();
        priority_queue<pair<long long,long long>, vector<pair<long long,long long>>, greater<pair<long long,long long>>> pqv;
        vector<vector<pair<long long,long long>>> vm3(m);
        for(int i=0; i<m; i++)
        {
            for(int j=0; j<n; j++)
            {
                pqv.push({board[i][j],j});
                if(pqv.size() > 3) pqv.pop();
            }
            while(!pqv.empty())
            {
                vm3[i].push_back(pqv.top());
                pqv.pop();
            }
        }
        long long res = -1e15;
        for(int i=0 ;i<m ;i++)
        {
            for(int j=i+1; j<m; j++)
            {
                for(int k=j+1; k<m; k++)
                {
                    for(int a=0; a<3; a++)
                    {
                        for(int b=0; b<3; b++)
                        {
                            for(int c=0; c<3; c++)
                            {
                                if(vm3[i][a].second != vm3[j][b].second && vm3[k][c].second != vm3[j][b].second && vm3[i][a].second != vm3[k][c].second)
                                {
                                    res = max(res, vm3[i][a].first + vm3[j][b].first + vm3[k][c].first);
                                }
                            }
                        }
                    }
                }
            }
        }
        return res;
    }
};
