class Solution {
    int m, n;
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        m = board.size();
        n = board[0].size();

        vector<vector<vector<int>>> rows(m);
        set<vector<int>> st;
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                st.insert({board[i][j], j});
                if(st.size() > 3) 
                    st.erase(st.begin());
            }

            for(auto &e: st)
                rows[i].push_back(e);

            st.clear();
        }

        long long ans = -1e18;
        for(int i = 0; i < m; i++) {
            for(int j = i+1; j < m; j++) {
                for(int k = j+1; k < m; k++) {
                    for(auto &x: rows[i]) {
                        for(auto &y: rows[j]) {
                            for(auto &z: rows[k]) {
                                if(x[1] == y[1] || x[1] == z[1] || y[1] == z[1]) continue;
                                ans = max(ans, (long long) x[0]+y[0]+z[0]);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
