class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int aaju = board.size();
        int baju = board[0].size();
        long long thes = LLONG_MIN;
        vector<vector<long long>> thot(aaju, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> lol(aaju, vector<int>(3, -1));

        for (int i = 0; i < aaju; ++i) {
            vector<pair<long long, int>> pol;
            for (int j = 0; j < baju; ++j) {
                pol.emplace_back(board[i][j], j);
            }
            sort(pol.rbegin(), pol.rend());

            for (int k = 0; k < 3 && k < baju; ++k) {
                thot[i][k] = pol[k].first;
                lol[i][k] = pol[k].second;
            }
        }

        for (int r1 = 0; r1 < aaju; ++r1) {
            for (int r2 = r1 + 1; r2 < aaju; ++r2) {
                for (int r3 = r2 + 1; r3 < aaju; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (lol[r2][j] == lol[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (lol[r3][k] == lol[r1][i] || lol[r3][k] == lol[r2][j]) continue;

                                long long currentSum = thot[r1][i] + thot[r2][j] + thot[r3][k];
                                thes = max(thes, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return thes;
    }
};
