class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();
        vector<vector<pair<int,int>>> top(n, vector<pair<int,int>> (3, {-1, -1e9})), bottom = top;
        for(int i = 0; i < n; i++) {
            vector<pair<int,int>> temp;
            for(int j = 0; j < m; j++) temp.emplace_back(board[i][j], j);
            sort(temp.rbegin(), temp.rend());
            temp.resize(3);
            if(i > 0) for(auto it : top[i - 1]) temp.push_back(it);
            sort(temp.rbegin(),temp.rend());
            vector<pair<int,int>> put = { temp[0] };
            set<int> done;
            done.insert(temp[0].second);
            for(int i=1;i<(int)temp.size();i++) {
                if(done.count(temp[i].second)) continue;
                put.push_back(temp[i]);
                done.insert(temp[i].second);
            }
            put.resize(3);
            top[i] = put;
        }
        for(int i = n - 1; i >= 0; i--) {
            vector<pair<int,int>> temp;
            for(int j = 0; j < m; j++) temp.emplace_back(board[i][j], j);
            sort(temp.rbegin(), temp.rend());
            temp.resize(3);
            if(i < n - 1) for(auto it : bottom[i + 1]) temp.push_back(it);
            sort(temp.rbegin(),temp.rend());
            vector<pair<int,int>> put = { temp[0] };
            set<int> done;
            done.insert(temp[0].second);
            for(int i=1;i<(int)temp.size();i++) {
                if(done.count(temp[i].second)) continue;
                put.push_back(temp[i]);
                done.insert(temp[i].second);
            }
            put.resize(3);
            bottom[i] = put;
        }

        long long ans = -1e18;
        for(int i = 1; i < n - 1; i++) {
            for(int j = 0; j < m; j++) {
                for(auto it1 : top[i - 1]) for(auto it2 : bottom[i + 1]) {
                    if(it1.second != j && it2.second != j && it1.second != it2.second) {
                        long long now = board[i][j];
                        now += it1.first;
                        now += it2.first;
                        ans = max(ans, now);
                    }
                }
            }
        }
        return ans;
    }
};
