class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int rows = board.size();
        int cols = board[0].size();

        // Store top 3 values for each row with their column indices
        vector<vector<pair<long long, int>>> topValues(rows);
        for (int r = 0; r < rows; ++r) {
            vector<pair<long long, int>> rowValues;
            for (int c = 0; c < cols; ++c) {
                rowValues.emplace_back(board[r][c], c);
            }
            sort(rowValues.rbegin(), rowValues.rend());
            topValues[r] = {rowValues[0], rowValues[1], rowValues[2]};
        }

        long long maxSum = LLONG_MIN;

        // Iterate over all combinations of rows
        for (int r1 = 0; r1 < rows - 2; ++r1) {
            for (int t1 = 0; t1 < 3; ++t1) {
                for (int r2 = r1 + 1; r2 < rows - 1; ++r2) {
                    for (int t2 = 0; t2 < 3; ++t2) {
                        if (topValues[r2][t2].second != topValues[r1][t1].second) {
                            for (int r3 = r2 + 1; r3 < rows; ++r3) {
                                for (int t3 = 0; t3 < 3; ++t3) {
                                    if (topValues[r3][t3].second != topValues[r2][t2].second && topValues[r3][t3].second != topValues[r1][t1].second) {
                                        long long currentSum = static_cast<long long>(topValues[r1][t1].first) + 
                                                              static_cast<long long>(topValues[r2][t2].first) + 
                                                              static_cast<long long>(topValues[r3][t3].first);
                                        maxSum = max(maxSum, currentSum);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;

    }
};
