class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size(), n = board[0].size();
        vector<set<pair<int, int>>> col(n);
        vector<set<pair<int, int>>> row(m);

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                col[j].insert({board[i][j], i});
                row[i].insert({board[i][j], j});
            }
        }

        long long maxi = -1e12;
        for(int i = 0; i < m; i++) {
            vector<vector<pair<int,int>>>vec(n);
            for(int j = 0; j < n; j++) {
                col[j].erase({board[i][j], i});
                auto iter = --col[j].end();
                vec[j].push_back(*iter);
                --iter;
                vec[j].push_back(*iter);
            }
            for(int j = 0; j < n; j++) {
                row[i].erase({board[i][j], j});
                for(int k = j + 1; k < n; k++) {
                    // cout << i << " " << j << " " << k << endl;
                    long long sum = 0ll;
                    row[i].erase({board[i][k], k});
                    auto it = --row[i].end();
                    sum = sum + it -> first;
                    row[i].insert({board[i][k], k});


                    auto large_j = vec[j][0];
                    auto large_k = vec[k][0];

                    if(large_j.second != large_k.second) {
                        sum = sum + large_j.first + large_k.first;
                    } else {
                        sum = sum + max(vec[j][0].first + vec[k][1].first, vec[j][1].first + vec[k][0].first);
                    }

                    maxi = max(maxi, sum);
                }
                row[i].insert({board[i][j], j});
            }

            for(int j = 0; j < n; j++) {
                col[j].insert({board[i][j], i});
            }
        }

        return maxi;
    }
};
