class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        mx = [sorted(zip(row, range(n)), reverse=True) for row in board]
        res = -inf
        def com(c1, c2, c3):
            t = c1[0][1]
            ans = c1[0][0]
            p1 = p2 = 0
            if c2[0][1] == t:
                a = c2[1][1]
                p1 += c2[1][0]
                if c3[0][1] != t and c3[0][1] != a: p1 += c3[0][0]
                elif c3[0][1] == t or c3[0][1] == a: p1 += c3[1][0] if c3[1][1] != t and c3[1][1] != a else c3[2][0]
            else:
                p1 += c2[0][0]
                a = c2[0][1]
                if c3[0][1] != t and c3[0][1] != a: p1 += c3[0][0]
                elif c3[0][1] == t or c3[0][1] == a: p1 += c3[1][0] if c3[1][1] != t and c3[1][1] != a else c3[2][0]
            if c3[0][1] == t:
                p2 += c3[1][0]
                a = c3[1][1]
                if c2[0][1] != t and c2[0][1] != a: p2 += c2[0][0]
                elif c2[0][1] == t or c2[0][1] == a: p2 += c2[1][0] if c2[1][1] != t and c2[1][1] != a else c2[2][0]
            else:
                p2 += c3[0][0]
                a = c3[0][1]
                if c2[0][1] != t and c2[0][1] != a: p2 += c2[0][0]
                elif c2[0][1] == t or c2[0][1] == a: p2 += c2[1][0] if c2[1][1] != t and c2[1][1] != a else c2[2][0]
            return ans + max(p1, p2)

        for i1 in range(m - 2):
            for i2 in range(i1 + 1, m - 1):
                for i3 in range(i2 + 1, m):
                    t1, t2, t3 = mx[i1], mx[i2], mx[i3]
                    res = max(res, com(t1, t2, t3), com(t2, t1, t3), com(t3, t1, t2))      
        return res
