class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        import copy
        rows = copy.copy(board)
        for i in range(m):
            for j in range(n):
                rows[i][j] = (rows[i][j], j)
            rows[i].sort(reverse=True)
        ans = float('-inf')
        for a in range(m):
            x = rows[a][:3]
            # print(x)
            for b in range(a + 1, m):
                y = rows[b][:3]
                for c in range(b + 1, m):
                    z = rows[c][:3]
                    res = float('-inf')
                    for u in x:
                        for i in y:
                            if i[1] != u[1]:
                                for o in z:
                                    if o[1] != i[1] and o[1] != u[1]:
                                        res = max(res, u[0] + i[0] + o[0])
                                        ans = max(ans, res)
        return ans
