class Solution {
public:
    long long ans=-1e15;
    long long maximumValueSum(vector<vector<int>>& board) {
        // priority_queue<vector<int>>pq;
        int n=board.size(),m=board[0].size();

        vector<vector<int>>vp;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                vp.push_back({board[i][j],i,j});
            }
        }

        sort(vp.rbegin(),vp.rend());

        for(int i=0;i<min(m*n,210);i++){
            for(int j=i+1;j<min(m*n,210);j++){
                if(vp[i][1]==vp[j][1] || vp[i][2]==vp[j][2])continue;
                for(int k=j+1;k<min(m*n,210);k++){
                    if(vp[i][1]==vp[k][1] || vp[i][2]==vp[k][2] || vp[j][1]==vp[k][1] || vp[j][2]==vp[k][2])continue;
                    ans=max(ans,1LL*(0LL+vp[i][0]+vp[j][0]+vp[k][0]));

                    // cout<<vp[i][1]<<vp[i][2]<<vp[j][1]<<vp[j][2]<<vp[k][1]<<vp[k][2]<<endl;
                }
            }
        }

        return ans;
    }
};
