func maximumValueSum(board [][]int) int64 {
    m, n := len(board), len(board[0])
    maxSum := int64(math.MinInt64)

    // 预处理每一行的最高的三个值及其对应的列索引
    top3Cols := make([][][2]int, m)
    for i := 0; i < m; i++ {
        // 找到当前行中前三个最大值和对应的列索引
        top3 := [][2]int{}
        for j := 0; j < n; j++ {
            top3 = append(top3, [2]int{board[i][j], j})
        }
        // 按照值从大到小排序
        sort.Slice(top3, func(a, b int) bool {
            return top3[a][0] > top3[b][0]
        })
        // 只保留前三个最大值
        if len(top3) > 3 {
            top3 = top3[:3]
        }
        top3Cols[i] = top3
    }

    // 枚举三行
    for i := 0; i < m; i++ {
        for j := i + 1; j < m; j++ {
            for k := j + 1; k < m; k++ {
                // 枚举每行的最高三个列组合
                for _, ai := range top3Cols[i] {
                    for _, bj := range top3Cols[j] {
                        if bj[1] == ai[1] {
                            continue // 保证列不同
                        }
                        for _, ck := range top3Cols[k] {
                            if ck[1] == ai[1] || ck[1] == bj[1] {
                                continue // 保证列不同
                            }
                            // 计算当前组合的值
                            sum := ai[0] + bj[0] + ck[0]
                            maxSum = max(maxSum, int64(sum))
                        }
                    }
                }
            }
        }
    }

    return maxSum
}

func max(a, b int64) int64 {
    if a > b {
        return a
    }
    return b
}
