class Solution {
public:
    //int dp[101][4][10];
    long long f(int i, int cnt,vector<vector<pair<int,int>>>& board,string &mask)
    {
        if(cnt>3)
        {
           return -1e18; 
        }
        if(cnt==3)
        {
            return 0;
        }

        if(i>=board.size())
        {
            if(cnt!=3)
            {
                return -1e18;
            }
            return 0;
        }



        long long ans=f(i+1,cnt,board,mask);

        for(int j=0; j<3; j++)
        {
            int t=board[i][j].second;
            if(mask[t]=='0')
            {
                mask[t]='1';
                ans=max(ans,board[i][j].first+f(i+1,cnt+1,board,mask));
                mask[t]='0';
            }
        }
        return  ans;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        string s;
        for(int i=0; i<100; i++)
        {
            s.push_back('0');
        }
        int n=board.size();
        int m=board[0].size();
        vector<vector<pair<int,int>>>v(n,vector<pair<int,int>>(m));
        for(int i=0; i<n; i++)
        {
            for(int j=0; j<m; j++)
            {
                v[i][j].first=board[i][j];
                v[i][j].second=j;
            }
        }
        //memset(dp,-1,sizeof dp);
        for(int i=0; i<n; i++)
        {
             sort(v[i].rbegin(),v[i].rend());
        }
        return f(0,0,v,s);
    }
};
